import React, { Component } from "react";
import { ReactDOM } from "react";
import './product.css'

import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faYoutube,
    faFacebook,
    faTwitter,
    faInstagram
  } from "@fortawesome/free-brands-svg-icons";
import { Outlet, Link } from "react-router-dom";
import axios from "axios";
class Product extends Component
{
    constructor(props) {
        super(props);
        this.slideRef = React.createRef();
        this.state = {
          data : [],
        }
    };
    componentDidMount(){
        let data ;
		axios.get('http://localhost:8000/products/')
		.then(res => {
            
			data = res.data.data;
			this.setState({
				data : data	

			});
		})
		.catch(err => {})
    };
   render(){
    return (
        <div class="main-page">
            <ul class="home-page-top">
                <li><a href="/">Home</a></li>
                <li><a href="news.asp">News</a></li>
                <li><a href="contact.asp">Contact</a></li>
                <li><a href="about.asp">About</a></li>
                <img src="https://cdn.pixabay.com/photo/2022/12/16/01/41/balloons-7658766_960_720.jpg" class="logo-right"></img>
            </ul>
            <section>
                <p class="mid_text">Get ready to for the extravaganza</p>
            </section>
            <img  src="https://cdn.pixabay.com/photo/2022/12/16/01/41/balloons-7658766_960_720.jpg" width={1510} height={1000} alt="kf"></img>
           <p class="products-text"> Our products</p>
           <section>
                {this.state.data.map((data, index)=> (
                            <div>
                                <section>
                                    <div class="pl-1"><img src="https://cdn.pixabay.com/photo/2022/12/16/01/41/balloons-7658766_960_720.jpg"></img></div>
                                    <p class="products-list">{data.product_type}</p>
                                    <p class="products-list">{data.flavour}</p>
                                    <p class="products-list">{data.size}</p>
                                </section>
                            </div>
                            
                            
                            
                        )

                        )
                        };
            
           </section>
        </div>
    )
    }
   
};

export default Product
