import React , {Component} from "react";
import { ReactDOM } from "react";
import './reviews.css'
import Product from "./products.js";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faYoutube,
    faFacebook,
    faTwitter,
    faInstagram
  } from "@fortawesome/free-brands-svg-icons";
import { Outlet, Link } from "react-router-dom";
import axios from "axios";
import { hover } from "@testing-library/user-event/dist/hover";
import { Slide } from 'react-slideshow-image';
import {  Button, Checkbox, Form, Input  } from 'antd';

function Reviews()
{
    
    const [code,setCode] = useState();
    const [submit,submitted] = useState(false);
    const [auth,setAuth] = useState(false);
    const[color,setColor] = useState("failure");
    const[name,setName] = useState();
    const[email,setEmail] = useState();
    const[phone,setPhone] = useState();
    const[feedback,setfeedback] = useState();
    const [value,setValue] = useState();
    const[slideImages,setslideImages]=useState([
        {
          url: 'https://images.unsplash.com/photo-1509721434272-b79147e0e708?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1500&q=80',
          caption: 'Slide 1'
        },
       
      ]);
   
    const componentDidMount = () => {
        
    
		axios.post('http://localhost:8000/reviews/add/' , {"name":name , "email":email,"phone":phone,"feedback":feedback})
		.then(res => {
			if(res.data["data"]==="success")
            {
                alert('added');
                window.location.reload();
                

            }
            else{
                alert('not added');   
                window.location.reload();       
            }
		})
		.catch(err => {})
        
	};
    const handleSubmit = (e) =>{
        e.preventDefault();
        console.log(name);
        console.log(feedback);
        componentDidMount();
    }


    return (     
        <div class="main-page">
                <section class="home-page-top">
                    <div class="logo-right"><img src="https://cdn.pixabay.com/photo/2022/12/16/01/41/balloons-7658766_960_720.jpg"></img></div>
                    
                    <span><a href="/">Home</a></span>
                    <div class="dropdown">
                        <span><a href="/products">Products</a></span>
                        <div class="dropdown_item">
                            <a href="/products/whey/">Whey</a>
                            <a href="/products/creatine/">Creatine</a>
                            <a href="/products/preworkout/">Pre Workout</a>
                        </div>
                    </div>
                    <span><a href="/contact-us">Contact</a></span>
                    <span><a href="/about">About</a></span>
                    <span><a href="/authenticity">Authenticity checker</a></span>
                    <span><a href="/reviews">Reviews</a></span>
                    
                </section>
            
        
        
                <section>
                    <p class="mid_text">Sign up for the newsletter for more details on your journey</p>
                </section>
                <div>
                        {slideImages.map((slideImage, index)=> (
                            <img src={slideImage.url} alt={slideImage.caption}></img>
                        )

                        )
                        };
                    
                       
                </div>
                <div>
                    
                       
                    <p class="jknb2">Show us the iron love!</p>
                </div>
                <div>
                    <form onSubmit={handleSubmit} class="feedback-form">
                        <label class="jknb">
                        Name:
                        <input
                            type="text"
                            onChange={(e)=>setName(e.target.value)}
                            required
                        />
                        </label>
                        <label class="jknb">
                        Phone Number:
                        <input
                            type="text"
                            onChange={(e)=>setPhone(e.target.value)}
                            required
                        />
                        </label>
                        <label class="jknb">
                        Email:
                        <input
                            type="text"
                            onChange={(e)=>setEmail(e.target.value)}
                        />
                        </label>
                        <label class="jknb">
                        Feedback:
                        <input
                            type="text"
                            onChange={(e)=>setfeedback(e.target.value)}
                            required
                        />
                        </label>
                       
                        <p >{color}</p>
                     </form>
                     <Form
                        name="basic"
                        labelCol={{
                        span: 8,
                        }}
                        wrapperCol={{
                        span: 16,
                        }}
                        style={{
                        maxWidth: 600,
                        }}
                        initialValues={{
                        remember: true,
                        }}
                        onFinish={onFinish}
                        onFinishFailed={onFinishFailed}
                        autoComplete="off"
                    >
                        <Form.Item
                        label="Username"
                        name="username"
                        rules={[
                            {
                            required: true,
                            message: 'Please input your username!',
                            },
                        ]}
                        >
                        <Input />
                        </Form.Item>

                        <Form.Item
                        label="Password"
                        name="password"
                        rules={[
                            {
                            required: true,
                            message: 'Please input your password!',
                            },
                        ]}
                        >
                        <Input.Password />
                        </Form.Item>

                        <Form.Item
                        name="remember"
                        valuePropName="checked"
                        wrapperCol={{
                            offset: 8,
                            span: 16,
                        }}
                        >
                        <Checkbox>Remember me</Checkbox>
                        </Form.Item>

